version.file:
for /f "delims=" %%x in ('dir /b *.exe') do set setup=%%x "%setup%" /extract:
move disk1.cab .Npackd
if "%npackd_cl%" equ "" set npackd_cl=..\com.googlecode.windows-package-manager.NpackdCL-1 set onecmd="%npackd_cl%\npackdcl.exe" "path" "--package=com.googlecode.windows-package-manager.NpackdInstallerHelper" "--versions=[1.1, 2)" for /f "usebackq delims=" %%x in (`%%onecmd%%`) do set npackdih=%%x call "%npackdih%\InstallMSI.bat" INSTALLDIR yes

msiexec.exe /qn /norestart /Lime .Npackd\MSI.log /x{80956555-A512-4190-9CAD-B000C36D6B6B}
msiexec.exe /qn /norestart /Lime .Npackd\MSI.log /x{873E4648-6F6E-47F6-A7B2-A6F8DFABDCE6}
msiexec.exe /qn /norestart /Lime .Npackd\MSI.log /x{EB4DF488-AAEF-406F-A341-CB2AAA315B90}
set err=%errorlevel%
type .Npackd\MSI.log
rem 3010=restart required
if %err% equ 3010 exit /b 0
if %err% neq 0 exit /b %err%

cd OpenHardwareMonitor
